var baseURL = window.location.protocol + "//" + window.location.hostname + (window.location.port ? ":" + window.location.port : ""); function iniciarBatalha(a) { var o = { NacaoExercitoBranco: parseInt(getNacaoExercitoBranco()), NacaoExercitoPreto: parseInt(getNacaoExercitoPreto()) }; $.ajax({ type: "post", data: JSON.stringify(o), dataType: "json", contentType: "application/json", url: baseURL + "/api/BatalhasAPI/IniciarBatalha/" + a }).done(function (o) { window.location.href = "/Batalhas/Tabuleiro/" + a }).fail(function (a) { swal({ title: "Alerta", text: a.responseText, icon: "warning" }) }) } function getNacaoExercitoBranco() { return $("#NacaoExercitoBranco").find(":selected").val() } function getNacaoExercitoPreto() { return $("#NacaoExercitoPreto").find(":selected").val() }